    <div className="recipe-list">
      <table>
        <thead>
          <th>Name</th>
          <th>Cuisine</th>
          <th>Photo</th>
          <th>Ingredients</th>
          <th>Preparation</th>
          <th>Actions</th>
          {recipes.map((recipe, index) => (
            <RecipeItem recipe={recipe} />
          ))} 
        </thead>
        <tbody>

        </tbody>
      </table>
    </div>
  );
}

export default RecipeList;
